mapper.select:
/** 회원 정보 가져오기 **/
select  user_cate,pg_merch_no,pg_tid,user_seq
FROM    tb_user
WHERE   user_id = #{userId}

/** Tid 정보 조회 **/ SELECT PGC.BIZ_NO , PGC.CORP_NO , PGC.comp_nm , PGC.PG_MERCH_NO , PGC.MERCH_NM , PTM.PG_TID , PTM.PG_TID_NM , PTM.CERT_TYPE , PGC.TAX_FLAG , PTM.PAY_MTD_SEQ , PTM.PAY_CHN_CATE , PGC.APPREQ_CHK_AMT , PTM.SVC_STAT , PGC.SVC_STAT PG_SVC_STAT FROM TB_PG_TID_MST PTM ,(SELECT CMP.COMP_NM , MST.PG_MERCH_NO , MST.MERCH_NM , CMP.TAX_FLAG , MST.APP_CHK_AMT , CMP.BIZ_NO , CMP.CORP_NO , MST.APPREQ_CHK_AMT , MST.SVC_STAT FROM TB_PG_MST MST , TB_COMPANY CMP WHERE MST.DEL_FLAG = 'N' AND MST.SVC_STAT = '00' AND CMP.DEL_FLAG = 'N' AND CMP.USE_FLAG = 'Y' AND MST.COMP_SEQ = CMP.COMP_SEQ) PGC WHERE PTM.DEL_FLAG = 'N' AND PTM.SVC_STAT = '00' AND PTM.PG_MERCH_NO = PGC.PG_MERCH_NO
AND PTM.PG_MERCH_NO = #{pgMerchNo}
AND PTM.PG_TID = #{pgTid}
AND PTM.PG_TID = #{pgTid}
