mapper.insert:
/** tb_transaction_card 에러 **/ insert into tb_transaction_error ( tran_seq, /* 거래 일련번호 */ tran_date, /* 거래일자 */ tran_time, /* 거래시간 */ tran_type, /* 거래구분 ( 정상결제 : 10 , 취소결제 : 40) */
tot_amt, /* 거래금액 */ currency, /* 통화 */ pay_chn_cate, /* 결제채널 */ card_num, /* 카드번호 */ card_issu_cd, /* 카드발급사코드 */ card_issu_nm, /* 카드발급사명 */
appr_date, /* 승인일자 */ appr_time, /* 승인시간 */ appr_req_ip, /* 승인요청ip */ appr_resp_cd, /* 승인응답코드 */ appr_resp_msg, /* 승인응답메시지 */
pg_seq, /* PG거래번호 */ product_cd, /* 상품코드 */ product_nm, /* 상품명 */ cust_id, /* 고객아이디 */ cust_nm, /* 고객명 */ cust_email, /* 고객이메일 */ cust_phone, /* 고객휴대폰 */
order_seq, /* 주문번호 */ merch_no, /* 원천차가맹점번호 */ pg_merch_no, /* PG 가맹점번호 */ tid, /* 터미널ID */ pg_tid, /* PG TID */
valid_thru, tran_cate, /* 할부개월수 */ ins_user /* 회원seq */
) VALUES ( #{tranSeq}, #{orderDate}, #{orderTime}, #{tranType},
#{totAmt}, #{currencyType}, #{payChnCate}, #{cardNo}, #{cardCode}, #{cardIssuNm},
#{tradeDate}, #{tradeTime}, #{ipAddr}, #{ApprovalCode}, #{ApprovalMsg}, #{pgSeq}, /* pgSeq */ #{goodsCode},
#{goodsName}, #{custId}, #{custName}, #{custEmail}, #{custPhone},
#{orderSeq}, #{merchantNo}, #{pgMerchNo}, #{storeId}, #{pgTid},
#{userSeq}, #{tranCate}, #{expDate}
)

