mapper.update:
UPDATE #{tableyd}
SET CNCL_REASON  = #{cnclReason}
, TRAN_STATUS  = #{tranStatus}
, CNCL_DT      = #{cnclDt}

    , ACC_CHK_FLAG = #{accChkFlag}

, MOD_DT       = DATE_FORMAT(now(),'%Y%m%d%H%i%S')
, MOD_USER     = #{modUser}
WHERE 1 = 1
AND TRAN_SEQ = #{tranSeq}

mapper.select:
/** 가맹점 할부 가능 개월 수 조회 **/
